import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';

const AuthRequiredModal = () => {
  const [isVisible, setIsVisible] = useState(true);
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const { language } = useLanguage();
  const { isDarkMode } = useTheme();

  // If user is authenticated, don't show modal
  useEffect(() => {
    if (currentUser) {
      setIsVisible(false);
    }
  }, [currentUser]);

  const handleLogin = () => {
    navigate('/login', { state: { from: location } });
  };

  const handleSignup = () => {
    navigate('/signup', { state: { from: location } });
  };

  const handleCancel = () => {
    navigate('/');
  };

  if (!isVisible) return null;

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 flex items-center justify-center z-50 backdrop-blur-sm bg-black/40"
          style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 1000 }}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            className={`relative rounded-xl p-6 shadow-xl max-w-md w-full mx-4 ${
              isDarkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
            }`}
          >
            <div className="flex flex-col items-center text-center">
              <div className="mb-4 p-2 rounded-full bg-blue-50 text-blue-500">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M2 18v3c0 .6.4 1 1 1h4v-3h3v-3h2l1.4-1.4a6.5 6.5 0 1 0-4-4Z"/>
                  <circle cx="16.5" cy="7.5" r=".5"/>
                </svg>
              </div>
              <h3 className="text-xl font-bold mb-2">
                {language === 'UA' ? 'Авторизація необхідна' : 'Authentication Required'}
              </h3>
              <p className={`mb-6 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                {language === 'UA'
                  ? 'Будь ласка, увійдіть або створіть обліковий запис, щоб отримати доступ до цієї функції.'
                  : 'Please sign in or create an account to access this feature.'}
              </p>
              <div className="flex flex-col sm:flex-row gap-3 w-full">
                <button
                  onClick={handleLogin}
                  className="px-4 py-2 rounded-lg bg-blue-primary text-white hover:bg-blue-600 transition-colors flex-1"
                >
                  {language === 'UA' ? 'Увійти' : 'Log In'}
                </button>
                <button
                  onClick={handleSignup}
                  className={`px-4 py-2 rounded-lg transition-colors flex-1 ${
                    isDarkMode 
                      ? 'bg-gray-700 text-white hover:bg-gray-600' 
                      : 'bg-gray-100 text-gray-900 hover:bg-gray-200'
                  }`}
                >
                  {language === 'UA' ? 'Зареєструватися' : 'Sign Up'}
                </button>
              </div>
              <button
                onClick={handleCancel}
                className="mt-4 text-sm text-blue-primary hover:underline"
              >
                {language === 'UA' ? 'Повернутися на головну' : 'Return to Home'}
              </button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default AuthRequiredModal;
