import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { isDarkMode } = useTheme();
  const { t } = useLanguage();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    // Clear error when user types
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: '',
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    const newErrors = {};
    if (!formData.firstName) newErrors.firstName = 'First name is required';
    if (!formData.lastName) newErrors.lastName = 'Last name is required';
    if (!formData.email) newErrors.email = 'Email is required';
    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setLoading(true);

    try {
      // Mock signup - replace with actual API call
      await new Promise((resolve) => setTimeout(resolve, 1000));

      navigate('/dashboard');
    } catch (error) {
      setErrors({ general: 'Registration failed. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col md:flex-row bg-theme-primary">
      {/* Left side - Brand */}
      <motion.div 
        className="w-full md:w-1/2 flex flex-col justify-center items-center p-8 bg-theme-primary"
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <motion.div 
              className="flex justify-center mx-auto"
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 400, damping: 10 }}
            >
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-16 h-16 text-blue-primary">
                <path d="M11.47 3.84a.75.75 0 011.06 0l8.69 8.69a.75.75 0 101.06-1.06l-8.689-8.69a2.25 2.25 0 00-3.182 0l-8.69 8.69a.75.75 0 001.061 1.06l8.69-8.69z" />
                <path d="M12 5.432l8.159 8.159c.03.03.06.058.091.086v6.198c0 1.035-.84 1.875-1.875 1.875H15a.75.75 0 01-.75-.75v-4.5a.75.75 0 00-.75-.75h-3a.75.75 0 00-.75.75V21a.75.75 0 01-.75.75H5.625a1.875 1.875 0 01-1.875-1.875v-6.198a2.29 2.29 0 00.091-.086L12 5.43z" />
              </svg>
            </motion.div>
            <motion.h2 
              className="mt-4 text-3xl font-bold text-theme-primary"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2, duration: 0.5 }}
            >
              {t.welcomeToMoveIN || "Welcome to MoveIN"}
            </motion.h2>
            <motion.p 
              className="mt-2 text-theme-secondary"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3, duration: 0.5 }}
            >
              {t.findYourHome || "Find your perfect home today"}
            </motion.p>
          </div>

          <div className="mt-8 relative">
            <motion.div
              className="h-64 w-full rounded-xl bg-gradient-to-br from-blue-primary/20 via-blue-primary/10 to-transparent shadow-xl overflow-hidden"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4, duration: 0.5 }}
            >
              <div className="absolute inset-0 bg-[url('./images/home-illustration.svg')] bg-center bg-no-repeat bg-contain opacity-70"></div>
            </motion.div>
          </div>
        </div>
      </motion.div>

      {/* Right side - Signup Form */}
      <motion.div 
        className="w-full md:w-1/2 flex items-center justify-center p-8"
        initial={{ opacity: 0, x: 50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="max-w-md w-full space-y-6 p-8 rounded-2xl shadow-lg border theme-card">
          <div>
            <h1 className="text-2xl font-bold text-theme-primary">{t.createAccount || "Create Account"}</h1>
            <p className="mt-2 text-sm text-theme-secondary">
              {t.alreadyHaveAccount || "Already have an account?"}{' '}
              <Link to="/login" className="font-medium text-blue-primary hover:text-blue-hover transition-colors">
                {t.signin || "Sign in"}
              </Link>
            </p>
          </div>

          <form className="space-y-6" onSubmit={handleSubmit}>
            {errors.general && (
              <motion.div 
                className="p-3 bg-red-500/10 border border-red-500/20 rounded-lg text-red-600 text-sm"
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
              >
                {errors.general}
              </motion.div>
            )}

            <div className="space-y-4">
              {/* Name fields in grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-theme-secondary mb-1">
                    {t.firstName || "First Name"}
                  </label>
                  <motion.div whileFocus={{ scale: 1.01 }} className="relative">
                    <input
                      id="firstName"
                      name="firstName"
                      type="text"
                      required
                      className={`appearance-none relative block w-full px-4 py-3 border ${
                        errors.firstName ? 'border-red-500' : 'border-theme'
                      } rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-primary/20 focus:border-blue-primary theme-input transition-colors`}
                      placeholder="John"
                      value={formData.firstName}
                      onChange={handleChange}
                    />
                    {errors.firstName && (
                      <motion.p 
                        className="mt-1 text-sm text-red-500"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                      >
                        {errors.firstName}
                      </motion.p>
                    )}
                  </motion.div>
                </div>

                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-theme-secondary mb-1">
                    {t.lastName || "Last Name"}
                  </label>
                  <motion.div whileFocus={{ scale: 1.01 }} className="relative">
                    <input
                      id="lastName"
                      name="lastName"
                      type="text"
                      required
                      className={`appearance-none relative block w-full px-4 py-3 border ${
                        errors.lastName ? 'border-red-500' : 'border-theme'
                      } rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-primary/20 focus:border-blue-primary theme-input transition-colors`}
                      placeholder="Doe"
                      value={formData.lastName}
                      onChange={handleChange}
                    />
                    {errors.lastName && (
                      <motion.p 
                        className="mt-1 text-sm text-red-500"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                      >
                        {errors.lastName}
                      </motion.p>
                    )}
                  </motion.div>
                </div>
              </div>

              <div>
                <label htmlFor="email" className="block text-sm font-medium text-theme-secondary mb-1">
                  {t.email || "Email Address"}
                </label>
                <motion.div whileFocus={{ scale: 1.01 }} className="relative">
                  <input
                    id="email"
                    name="email"
                    type="email"
                    required
                    className={`appearance-none relative block w-full px-4 py-3 border ${
                      errors.email ? 'border-red-500' : 'border-theme'
                    } rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-primary/20 focus:border-blue-primary theme-input transition-colors`}
                    placeholder="you@example.com"
                    value={formData.email}
                    onChange={handleChange}
                  />
                  {errors.email && (
                    <motion.p 
                      className="mt-1 text-sm text-red-500"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                    >
                      {errors.email}
                    </motion.p>
                  )}
                </motion.div>
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-theme-secondary mb-1">
                  {t.password || "Password"}
                </label>
                <motion.div whileFocus={{ scale: 1.01 }} className="relative">
                  <input
                    id="password"
                    name="password"
                    type="password"
                    required
                    className={`appearance-none relative block w-full px-4 py-3 border ${
                      errors.password ? 'border-red-500' : 'border-theme'
                    } rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-primary/20 focus:border-blue-primary theme-input transition-colors`}
                    placeholder="••••••••"
                    value={formData.password}
                    onChange={handleChange}
                  />
                  {errors.password && (
                    <motion.p 
                      className="mt-1 text-sm text-red-500"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                    >
                      {errors.password}
                    </motion.p>
                  )}
                </motion.div>
              </div>

              <div>
                <label htmlFor="confirmPassword" className="block text-sm font-medium text-theme-secondary mb-1">
                  {t.confirmPassword || "Confirm Password"}
                </label>
                <motion.div whileFocus={{ scale: 1.01 }} className="relative">
                  <input
                    id="confirmPassword"
                    name="confirmPassword"
                    type="password"
                    required
                    className={`appearance-none relative block w-full px-4 py-3 border ${
                      errors.confirmPassword ? 'border-red-500' : 'border-theme'
                    } rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-primary/20 focus:border-blue-primary theme-input transition-colors`}
                    placeholder="••••••••"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                  />
                  {errors.confirmPassword && (
                    <motion.p 
                      className="mt-1 text-sm text-red-500"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                    >
                      {errors.confirmPassword}
                    </motion.p>
                  )}
                </motion.div>
              </div>
            </div>

            <div className="pt-2">
              <motion.button
                type="submit"
                className="group relative w-full flex justify-center py-3 px-4 border border-transparent rounded-lg text-white bg-blue-primary hover:bg-blue-hover focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-primary transition-colors"
                disabled={loading}
                whileHover={{ scale: 1.01 }}
                whileTap={{ scale: 0.99 }}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.1, duration: 0.3 }}
              >
                {loading ? (
                  <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                ) : null}
                {loading ? t.signingUp || "Creating account..." : t.signUp || "Create account"}
              </motion.button>
            </div>
          </form>

          <p className="text-xs text-center text-theme-tertiary mt-6">
            {t.bySigningUp || "By signing up, you agree to our"}{' '}
            <Link to="/terms" className="text-blue-primary hover:text-blue-hover">
              {t.terms || "Terms of Service"}
            </Link>{' '}
            {t.and || "and"}{' '}
            <Link to="/privacy" className="text-blue-primary hover:text-blue-hover">
              {t.privacy || "Privacy Policy"}
            </Link>
          </p>
        </div>
      </motion.div>
    </div>
  );
};

export default Signup;
