import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence, useScroll, useInView } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Navbar from '../components/Navbar';
import PropertyPhotoTour from '../components/PropertyPhotoTour';

const PropertyDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isDarkMode } = useTheme();
  const { language } = useLanguage();
  const [property, setProperty] = useState(null);
  const [similarProperties, setSimilarProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  const [photoTourOpen, setPhotoTourOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  
  const { scrollYProgress } = useScroll();
  const descriptionRef = useRef(null);
  const isDescriptionInView = useInView(descriptionRef, { once: true, amount: 0.3 });
  const amenitiesRef = useRef(null);
  const isAmenitiesInView = useInView(amenitiesRef, { once: true, amount: 0.3 });
  const similarRef = useRef(null);
  const isSimilarInView = useInView(similarRef, { once: true, amount: 0.3 });
  
  // Detect scroll for animations
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Mock data for a sample property
  const mockProperty = {
    id: '1',
    title: 'Сучасна 3-кімнатна квартира',
    price: 57409,
    address: 'вулиця Сергія Єфремова, 26',
    area: 150,
    rooms: 3,
    floor: 2,
    totalFloors: 4,
    buildYear: 2018,
    buildingType: 'монолітно-каркасний',
    heating: 'індивідуальне опалення',
    agent: {
      name: 'Стасюк Тетяна',
      agency: 'Sitalo Real Estate',
      phone: '+380 67 XXX XXXX',
      verified: true,
      photo: 'https://via.placeholder.com/50'
    },
    photos: [
      'https://source.unsplash.com/800x600/?apartment,1',
      'https://source.unsplash.com/800x600/?apartment,2',
      'https://source.unsplash.com/800x600/?apartment,3',
      'https://source.unsplash.com/800x600/?apartment,4',
      'https://source.unsplash.com/800x600/?apartment,5',
      'https://source.unsplash.com/800x600/?apartment,6',
      'https://source.unsplash.com/800x600/?apartment,7',
      'https://source.unsplash.com/800x600/?apartment,8',
    ],
    description: `Оренда чудової 3-кімнатної квартири в новобудові еліт класу по вул. Єфремова.
Поряд головного корпусу Політехнічного Університету.
Помешкання знаходиться на 2 поверсі 4-поверхового будинку.
Загальна площа - 150 м2.
Будинок клубного типу на 10 квартир, оздоблення сходової - польський мармур, панорамні вікна, єдиний новобуд Львова з люкс оформленням сходової клітини.
Ліфт, дві квартири на поверсі, зручний поверх.
Парковка в закритому дворі під охороною на два авто.
Планування:
- Велика простора кухня-вітальня;
- Три окремих спальні;
- Два санвузли;
- Гардеробна;
- Балкон.`,
    amenities: [
      'Душова кабіна',
      'Лічильники',
      'Кондиціонер',
      'Посуд',
      'Мікрохвильовка',
      'Пральна машина',
      'Ліжко',
      'Холодильник',
      'Шафа',
      'Телевізор'
    ],
    viewed: 'знайдено сьогодні о 09:41',
    updated: 'оновлено сьогодні о 13:57',
    commission: '100%',
    photoCount: 25
  };

  // Mock data for similar properties
  const mockSimilarProperties = [
    {
      _id: '2',
      title: '3-кімнатна квартира на вул. Кирила і Мефодія',
      price: 57409,
      location: { city: 'Львів', address: 'вулиця Кирила і Мефодія' },
      area: 130,
      bedrooms: 3,
      bathrooms: 2,
      propertyType: 'Apartment',
      listingType: 'rent',
      images: ['https://source.unsplash.com/800x600/?apartment,10']
    },
    {
      _id: '3',
      title: 'Простора квартира на вул. Євгена Коновальця',
      price: 63007,
      location: { city: 'Львів', address: 'вулиця Євгена Коновальця' },
      area: 140,
      bedrooms: 3,
      bathrooms: 2,
      propertyType: 'Apartment',
      listingType: 'rent',
      images: ['https://source.unsplash.com/800x600/?apartment,11']
    },
    {
      _id: '4',
      title: 'Затишна квартира на вул. Василя Стефаника',
      price: 58807,
      location: { city: 'Львів', address: 'вулиця Василя Стефаника, 7' },
      area: 125,
      bedrooms: 3,
      bathrooms: 1,
      propertyType: 'Apartment',
      listingType: 'rent',
      images: ['https://source.unsplash.com/800x600/?apartment,12']
    },
    {
      _id: '5',
      title: 'Сучасна квартира на вул. Січових Стрільців',
      price: 63007,
      location: { city: 'Львів', address: 'вулиця Січових Стрільців, 12' },
      area: 145,
      bedrooms: 3,
      bathrooms: 2,
      propertyType: 'Apartment',
      listingType: 'rent',
      images: ['https://source.unsplash.com/800x600/?apartment,13']
    }
  ];
  
  useEffect(() => {
    // In a real application, this would be an API call
    setTimeout(() => {
      setProperty(mockProperty);
      setSimilarProperties(mockSimilarProperties);
      setLoading(false);
    }, 800);
  }, [id]);

  if (loading) {
    return (
      <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
        <Navbar />
        <div className="container mx-auto px-4 py-8 flex justify-center items-center">
          <div className="w-10 h-10 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (!property) {
    return (
      <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
        <Navbar />
        <div className="container mx-auto px-4 py-8 text-center">
          <h2 className="text-2xl font-bold mb-4">
            {language === 'UA' ? 'Об\'єкт не знайдено' : 'Property not found'}
          </h2>
          <button
            onClick={() => navigate(-1)}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg"
          >
            {language === 'UA' ? 'Повернутись назад' : 'Go back'}
          </button>
        </div>
      </div>
    );
  }

  const handleOpenPhotoTour = () => {
    setPhotoTourOpen(true);
  };

  const handleClosePhotoTour = () => {
    setPhotoTourOpen(false);
  };

  const handlePrevImage = () => {
    setActiveImageIndex((prev) => (prev === 0 ? property.photos.length - 1 : prev - 1));
  };

  const handleNextImage = () => {
    setActiveImageIndex((prev) => (prev === property.photos.length - 1 ? 0 : prev + 1));
  };

  // Formats price with spaces instead of commas (Ukrainian style)
  const formatPrice = (price) => {
    return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  };

  // The updated return statement with actual content
  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
      <Navbar />
      
      <div className="container mx-auto px-4 py-8">
        {/* Breadcrumbs */}
        <div className="text-sm breadcrumbs mb-4">
          <Link to="/" className="text-blue-600 hover:underline">Головна</Link>
          <span className="mx-2">{'>'}</span>
          <Link to="/rent" className="text-blue-600 hover:underline">Оренда квартир Львів</Link>
          <span className="mx-2">{'>'}</span>
          <span className="text-gray-600">{property.address}</span>
        </div>
        
        {/* Property images gallery */}
        <div className="relative mb-8">
          {property.commission && (
            <div className="absolute top-4 left-4 z-10 bg-green-600 text-white px-3 py-1 rounded-lg text-sm font-medium">
              комісія {property.commission}
            </div>
          )}
          
          <div className="relative h-96 bg-gray-300 rounded-lg overflow-hidden">
            <img 
              src={property.photos[activeImageIndex]} 
              alt={`Property ${activeImageIndex + 1}`}
              className="w-full h-full object-cover"
            />
            
            <button 
              onClick={handlePrevImage}
              className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow-md"
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M15 18l-6-6 6-6" />
              </svg>
            </button>
            
            <button 
              onClick={handleNextImage}
              className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow-md"
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M9 18l6-6-6-6" />
              </svg>
            </button>
            
            <button 
              onClick={handleOpenPhotoTour}
              className="absolute bottom-4 right-4 bg-white/80 hover:bg-white text-gray-800 px-3 py-1 rounded-lg shadow-md text-sm font-medium flex items-center"
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1">
                <rect x="3" y="3" width="18" height="18" rx="2" ry="2" />
                <circle cx="8.5" cy="8.5" r="1.5" />
                <polyline points="21 15 16 10 5 21" />
              </svg>
              {property.photoCount} фотографій
            </button>
          </div>
          
          {/* Thumbnail preview */}
          <div className="flex mt-2 space-x-2 overflow-x-auto pb-2">
            {property.photos.map((photo, index) => (
              <button 
                key={index}
                onClick={() => setActiveImageIndex(index)}
                className={`flex-shrink-0 w-24 h-16 rounded-md overflow-hidden ${
                  index === activeImageIndex ? 'ring-2 ring-blue-600' : 'opacity-70'
                }`}
              >
                <img src={photo} alt={`Thumbnail ${index + 1}`} className="w-full h-full object-cover" />
              </button>
            ))}
          </div>
        </div>
        
        {/* Main content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left column */}
          <div className="lg:col-span-2">
            {/* Price and address */}
            <div className="mb-6">
              <h1 className="text-3xl font-bold mb-2">{formatPrice(property.price)} грн</h1>
              <p className="text-lg">{property.address}</p>
            </div>
            
            {/* Property details */}
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-6">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" />
                  <polyline points="9 22 9 12 15 12 15 22" />
                </svg>
                <span>{property.rooms} кімнати</span>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <rect x="3" y="3" width="18" height="18" rx="2" ry="2" />
                </svg>
                <span>{property.area} м²</span>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <rect x="2" y="3" width="20" height="14" rx="2" ry="2" />
                  <line x1="8" y1="21" x2="16" y2="21" />
                  <line x1="12" y1="17" x2="12" y2="21" />
                </svg>
                <span>поверх {property.floor} з {property.totalFloors}</span>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <rect x="3" y="4" width="18" height="18" rx="2" ry="2" />
                  <line x1="16" y1="2" x2="16" y2="6" />
                  <line x1="8" y1="2" x2="8" y2="6" />
                  <line x1="3" y1="10" x2="21" y2="10" />
                </svg>
                <span>{property.buildYear} рік будівництва</span>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <path d="M2 22h20" />
                  <path d="M6 18V3" />
                  <path d="M18 18V3" />
                  <path d="M6 11h12" />
                </svg>
                <span>{property.buildingType}</span>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-blue-600">
                  <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
                </svg>
                <span>{property.heating}</span>
              </div>
            </div>
            
            {/* Property description */}
            <div className="mb-6">
              <h2 className="text-xl font-bold mb-4">Опис</h2>
              <div className="whitespace-pre-line">
                {property.description}
              </div>
            </div>
            
            {/* Amenities */}
            <div className="mb-6">
              <h2 className="text-xl font-bold mb-4">В квартирі є</h2>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                {property.amenities.map((amenity, index) => (
                  <div key={index} className="flex items-center">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 text-green-600">
                      <polyline points="20 6 9 17 4 12" />
                    </svg>
                    <span>{amenity}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
          
          {/* Right column */}
          <div>
            {/* Agent info */}
            <div className={`p-6 rounded-lg shadow-md mb-6 ${isDarkMode ? 'bg-gray-800' : 'bg-white'}`}>
              <div className="flex items-center mb-4">
                <img src={property.agent.photo} alt={property.agent.name} className="w-12 h-12 rounded-full mr-3" />
                <div>
                  <h3 className="font-bold">{property.agent.name}</h3>
                  <p className="text-blue-600">{property.agent.agency}</p>
                  {property.agent.verified && (
                    <div className="flex items-center text-xs text-gray-500">
                      <span>Верифікована особа</span>
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="ml-1 text-green-600">
                        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14" />
                        <polyline points="22 4 12 14.01 9 11.01" />
                      </svg>
                    </div>
                  )}
                </div>
              </div>
              
              {/* Contact buttons */}
              <div className="space-y-3">
                <a href={`tel:${property.agent.phone}`} className="block w-full py-3 bg-blue-600 text-white text-center rounded-lg font-medium hover:bg-blue-700 transition-colors">
                  {property.agent.phone}
                </a>
                <a href="#" className="block w-full py-3 border border-blue-600 text-blue-600 text-center rounded-lg font-medium hover:bg-blue-50 transition-colors">
                  Написати
                </a>
              </div>
            </div>
            
            {/* Additional info */}
            <div className={`p-6 rounded-lg shadow-md ${isDarkMode ? 'bg-gray-800' : 'bg-white'}`}>
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center text-gray-500">
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1">
                    <circle cx="12" cy="12" r="10" />
                    <polyline points="12 6 12 12 16 14" />
                  </svg>
                  <span>{property.viewed}</span>
                </div>
                <button className="text-blue-600 hover:underline">
                  Поскаржитись на оголошення
                </button>
              </div>
              <div className="flex items-center text-gray-500">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1">
                  <circle cx="12" cy="12" r="10" />
                  <line x1="12" y1="8" x2="12" y2="12" />
                  <line x1="12" y1="16" x2="12.01" y2="16" />
                </svg>
                <span>{property.updated}</span>
              </div>
            </div>
          </div>
        </div>
        
        {/* Similar properties section */}
        <div className="mt-16">
          <h2 className="text-2xl font-bold mb-6">Ще 3-кімнатні квартири ~ {formatPrice(property.price)} грн поруч</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {similarProperties.map(property => (
              <div 
                key={property._id} 
                className={`rounded-lg overflow-hidden shadow-md ${isDarkMode ? 'bg-gray-800' : 'bg-white'}`}
              >
                <Link to={`/property/${property._id}`} className="block relative">
                  <img 
                    src={property.images[0]} 
                    alt={property.title} 
                    className="w-full h-48 object-cover"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4">
                    <div className="text-white font-bold">{formatPrice(property.price)} грн</div>
                  </div>
                </Link>
                <div className="p-4">
                  <Link 
                    to={`/property/${property._id}`} 
                    className="font-medium hover:text-blue-600 transition-colors line-clamp-2 h-12"
                  >
                    {property.title}
                  </Link>
                  <p className="text-gray-500 mt-2 text-sm">{property.location.address}</p>
                  <div className="flex justify-between mt-3 text-sm">
                    <div className="flex items-center">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1 text-gray-500">
                        <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
                        <polyline points="9 22 9 12 15 12 15 22"></polyline>
                      </svg>
                      <span>{property.bedrooms} кім.</span>
                    </div>
                    <div className="flex items-center">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-1 text-gray-500">
                        <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                      </svg>
                      <span>{property.area} м²</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      
      {/* Property Photo Tour */}
      <PropertyPhotoTour 
        photos={property?.photos || []} 
        isOpen={photoTourOpen} 
        onClose={handleClosePhotoTour} 
      />
    </div>
  );
};

export default PropertyDetail;
